mapper.select:
select a.MBR_NO as mbrNo , a.MBR_PHONE_NO as mbrPhoneNo , a.MBR_UUID as mbrUuid , a.MBR_NAME as mbrName , a.MBR_PHOTO_URL as mbrPhotoUrl , a.MBR_GENDER as mbrGender , a.MBR_REG_DATE as mbrRegDate , a.LAST_LOGIN_DATE as lastLoginDate , a.MBR_UPDATE_DATE as mbrUpdateDate , b.START_RANGE as startRange , b.END_RANGE as endRange , b.PUSH_YN as pushYn , b.ROOM_MBR_NUM_LIMIT as roomMbrNumLimit , b.SAFE_GUARD_PHONE_NO as safeGuardPhoneNo , b.SAFE_GUARD_NAME as safeGuardName , b.SAFE_TIME_INTERVAL as safeTimeInterval , c.ACCOUNT_NO as accountNo , c.ACCOUNT_EMAIL as accountEmail , c.ACCOUNT_ST as accountSt , d.KEYWORD_NO as keywordNo , d.KEYWORD_NAME as keywordName , d.KEYWORD_ST as keywordSt from MBR a left join SETTING b on a.MBR_NO = b.MBR_NO left join ACCOUNT c on a.MBR_NO = c.MBR_NO left join ( select c.MBR_NO , c.KEYWORD_NO , d.KEYWORD_NAME , d.KEYWORD_ST from KEYWORD_REL c , KEYWORD d where 1 = 1 and c.KEYWORD_NO = d.KEYWORD_NO ) d on a.MBR_NO = d.MBR_NO where 1 = 1 -- and a.MBR_NO = b.MBR_NO
and a.MBR_NO = #{mbrNo}
and a.MBR_PHONE_NO = #{mbrPhoneNo} and a.MBR_UUID = #{mbrUuid}
and 1 != 1

mapper.update:
    update 	
    		MBR
    set 	
    		MBR_PHONE_NO 	= #{mbrPhoneNo} 
	and 	MBR_UUID 		= #{mbrUuid}
	where 	
			1 = 1
	and 	MBR_NO = #{mbrNo}


update MBR
set MBR_PHOTO_URL = #{mbrPhotoUrl}
where MBR_NO = #{mbrNo}
