mapper.insert:
insert into ROOM_MBR
	( ROOM_NO, MBR_NO, GCM_REG_ID
	, ROOM_MBR_NO, ROOM_MBR_ST
	, REL_FRND_PHONE_NO
	, MUTUAL_YN, ROOM_MBR_RANK)
values
	( #{roomNo}, #{mbrNo}, #{gcmRegId}
	, #{roomMbrNo}, #{roomMbrSt}
	, #{relFrndPhoneNo}
	, #{mutualYn}, #{roomMbrRank} )

mapper.delete:
delete from 
		ROOM_MBR
where	1 = 1

	and	MBR_NO = #{mbrNo}


	and ROOM_NO = #{roomNo}


	and ROOM_NO in

					#{item.roomNo}

mapper.update:
    update 	
    		ROOM_MBR
    set 
    		MBR_NO = #{changeMbrNo}
	where
			1 = 1
	and 	ROOM_NO = #{roomNo}  
	and 	MBR_NO 	= #{originMbrNo}
